<script>
// import "../../main";
import { required, email } from "vuelidate/lib/validators";
export default {
    name: "Contact",
    data() {
        return {
            isDisabled: true,
            termConditionError: false,
            contactForm: {
                name: "",
                email: "",
                phone: "",
                comment: "",
            },
        };
    },
    validations: {
        contactForm: {
            name: { required },
            phone: { required },
            email: { required, email },
            comment: { required },
        },
    },
    methods: {
        submit() {
            if (this.isDisabled === false) {
                this.$v.contactForm.$touch();
                if (!this.$v.contactForm.$error) {
                    $(".contact-btn").html('<i class="fas fa-circle-notch fa-spin fast-spin"></i>');
                    axios
                        .post("/api/contact/email", this.contactForm)
                        .then((response) => response)
                        .then((response) => {
                            if (response.data.success === true) {
                                this.$toastr.s(response.data.message);
                                $(".contact-btn").html("Submit");
                            } else {
                                $.each(response.data.message, function (key, value) {
                                    this.$toastr.e(value);
                                });
                            }
                        });
                }
            } else {
                this.termConditionError = true;
            }
        },
        validateTerm() {
            if ($(".term").is(":checked")) {
                this.isDisabled = false;
                this.termConditionError = false;
            } else {
                this.isDisabled = true;
                this.termConditionError = true;
            }
        },
    },
};
</script>

<style>
.fast-spin {
    -webkit-animation: fa-spin 1s infinite linear;
    animation: fa-spin 1s infinite linear;
}
.input-field.error,
.input-field:invalid {
    box-shadow: unset !important;
}
</style>
